mapper.insert:
INSERT INTO code (
	ccName
	,ccNameEng
	,codeGroup_ccgSeq
	,ccOrder
	,ccUseNy
	,ccDelNy
	,ccRegDatetime
)
VALUES (
	#{ccName}
	,#{ccNameEng}
	,#{codeGroup_ccgSeq}
	,#{ccOrder}
	,#{ccUseNy}
	,#{ccDelNy}
	,#{ccRegDatetime}
)

		SELECT last_insert_id()

mapper.update:
UPDATE code
SET
	ccName = #{ccName}
	,ccNameEng = #{ccNameEng}
	,ccOrder = #{ccOrder}
	,ccUseNy = #{ccUseNy}
	,ccDelNy = #{ccDelNy}
	,ccRegDatetime = #{ccRegDatetime}
WHERE
	ccSeq = #{ccSeq}
