mapper.sql:
FROM codeGroup a
WHERE 1=1
	AND ccgDelNy = #{shDelNy}

		AND ccgSeq = #{shValue}
		AND ccgName LIKE CONCAT('%',#{shValue},'%')
		AND ccgNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO codeGroup (
	ccgName
	,ccgNameEng
	,ccgOrder
	,ccgUseNy
	,ccgDelNy
	,ccgRegDatetime
)
VALUES (
	#{ccgName}
	,#{ccgNameEng}
	,#{ccgOrder}
	,#{ccgUseNy}
	,#{ccgDelNy}
	,#{ccgRegDatetime}
)

		SELECT last_insert_id()

mapper.update:
UPDATE codeGroup
SET
	ccgName = #{ccgName}
	,ccgNameEng = #{ccgNameEng}
	,ccgOrder = #{ccgOrder}
	,ccgUseNy = #{ccgUseNy}
	,ccgDelNy = #{ccgDelNy}
	,ccgRegDatetime = #{ccgRegDatetime}
WHERE
	ccgSeq = #{ccgSeq}
