mapper.insert:
INSERT INTO item (
	member_mmSeq
	,itTitle
	,itServiceDetail
	,category1
	,category2
	,skillLevel
	,teamSize
	,serviceCategory
	,residency
	,developLanguage
	,provisionProcedure
	,clientPrepare
)
VALUES (
	#{sessSeq}
	,#{itTitle}
	,#{itServiceDetail}
	,#{category1}
	,#{category2}
	,#{skillLevel}
	,#{teamSize}
	,#{serviceCategory}
	,#{residency}
	,#{developLanguage}
	,#{provisionProcedure}
	,#{clientPrepare}
)

		SELECT last_insert_id()

INSERT INTO itemOption (
	member_mmSeq
	,item_itemSeq
	,responsiveWeb
	,contentUpload
	,sourceCode
	,design
	,functionPlus
	,pagePlus
	,quickWork
	,plusMod
	,itemPriceTitle
	,itemPriceDetail
	,itemPrice
	,numberOfPage
	,workPeriod
	,numberOfMod
)
VALUES (
	#{sessSeq}
	,#{itemSeq}
	,#{responsiveWeb}
	,#{contentUpload}
	,#{sourceCode}
	,#{design}
	,#{functionPlus}
	,#{pagePlus}
	,#{quickWork}
	,#{plusMod}
	,#{itemPriceTitle}
	,#{itemPriceDetail}
	,#{itemPrice}
	,#{numberOfPage}
	,#{workPeriod}
	,#{numberOfMod}
)

		SELECT last_insert_id()

mapper.update:
UPDATE item a
JOIN itemOption b on a.itemSeq = b.item_itemSeq
SET
	a.itTitle = #{itTitle}
	,a.itServiceDetail = #{itServiceDetail}
	,a.category1 = #{category1}
	,a.category2 = #{category2}
	,a.skillLevel = #{skillLevel}
	,a.teamSize = #{teamSize}
	,a.serviceCategory = #{serviceCategory}
	,a.residency = #{residency}
	,a.developLanguage = #{developLanguage}
	,a.provisionProcedure = #{provisionProcedure}
	,a.clientPrepare = #{clientPrepare}
	,b.responsiveWeb = #{responsiveWeb}
	,b.contentUpload = #contentUpload{}
	,b.sourceCode = #{sourceCode}
	,b.design = #{design}
	,b.functionPlus = #{functionPlus}
	,b.pagePlus = #{pagePlus}
	,b.quickWork = #{quickWork}
	,b.plusMod = #{plusMod}
	,b.itemPriceTitle = #{itemPriceTitle}
	,b.itemPriceDetail = #{itemPriceDetail}
	,b.itemPrice = #{itemPrice}
	,b.numberOfPage = #{numberOfPage}
	,b.workPeriod = #{workPeriod}
	,b.numberOfMod = #{numberOfMod}
WHERE
	itemSeq = #{itemSeq}
