mapper.select:
SELECT a.* FROM member a WHERE 1=1
AND mmDelNy = #{shDelNy}
AND mmName LIKE CONCAT('%',#{shValue},'%') AND mmRank LIKE CONCAT('%',#{shValue},'%') AND mmEmail LIKE CONCAT('%',#{shValue},'%') AND mmNickname LIKE CONCAT('%',#{shValue},'%') AND mmPw LIKE CONCAT('%',#{shValue},'%') AND mmJob LIKE CONCAT('%',#{shValue},'%') AND mmGender LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO member (
	mmName
	,mmNickname
	,mmEmail
	,mmEmailSelect
	,mmPw
	,mmBod
	,mmGender
	,mmZip
	,mmAddress1
	,mmAddress2
	,mmDelNy
	,mmRank
	,mmJob
	,mmInterest
	,mmSpecial
	,mmDetail
	,mmTechnique
	,mmCareer
	,mmSchool
	,mmSchoolMajor
	,mmSchoolState
	,mmCertificate
	,mmWishPay
	,mmWorkTime
	,mmIntroduce
	,mmCash
)
VALUES (
	#{mmName}
	,#{mmNickname}
	,#{mmEmail}
	,#{mmEmailSelect}
	,#{mmPw}
	,#{mmBod}
	,#{mmGender}
	,#{mmZip}
	,#{mmAddress1}
	,#{mmAddress2}
	,#{mmDelNy}
	,#{mmRank}
	,#{mmJob}
	,#{mmInterest}
	,#{mmSpecial}
	,#{mmDetail}
	,#{mmTechnique}
	,#{mmCareer}
	,#{mmSchool}
	,#{mmSchoolMajor}
	,#{mmSchoolState}
	,#{mmCertificate}
	,#{mmWishPay}
	,#{mmWorkTime}
	,#{mmIntroduce}
	,#{mmCash}
)

		SELECT last_insert_id()
